import React,{useState} from "react"
import dataarr from "./data"
import { useHistory } from "react-router-dom";


export default function Search(){
    let history = useHistory();
    const [name,setName]=useState('')
    const [dataA,setData]=useState(history.location.state?history.location.state[0]:[])
    const [submit,setSubmit]=useState(false)

    const fetchData=()=>{
        let data=[...dataA]
     dataarr.map(s=>{

        if(s.name.toLowerCase().includes(name.toLowerCase())){

                data.push(s)
     }

    })
 setData(data)
 setSubmit(true)
    console.log(data)
}

const newest=()=>{
    let data=[...dataA]
    data=data.sort((a, b) => {
        const aDate = new Date(a.date + ' ' + a.time)
        const bDate = new Date(b.date + ' ' + b.time)

        return bDate.getTime() - aDate.getTime()
      })
}

const top=()=>{
    let data=[...dataA]
    data.sort((a, b) => b.upvotes - a.upvotes)
    setData(data)
}
    return(
        <>
    {console.log(dataA)}
          <input type="text" value={name} onChange={(e)=>{setName(e.target.value)}} className="input1"/>
         <button disabled={name.length<3} onClick={fetchData}>Fetch</button>
         {dataA.length?
         <>
         <button onClick={newest}>Newest</button>
         <button onClick={top}>Top</button>
         <table>
         <tr>
           <th>ArticleId</th>
           <th>Name</th>
           <th>Title</th>
           <th>Upvotes</th>
           <th>Date</th>
         </tr>
         {dataA.map((i,index)=>
            (
                <tr>
                <td >{i.articleId}</td>
                <td>{i.name}</td>
                <td onClick={()=>history.push({pathname:"/desc",state:[i,dataA]})}> {i.title} </td>

                <td>{i.upvotes}</td>
                <td>{i.date}</td>
              </tr>
            ))}


       </table>
       </>
       :submit&&dataA?(<h3>No Results found</h3>):null}

        </>
    )
}
