import React,{useState} from "react"
import dataarr from "./data"

export default function Search(){
    const [name,setName]=useState('')
    const [dataA,setData]=useState([])
    const [submit,setSubmit]=useState(false)

    const fetchData=()=>{
        let data=[...dataA]
     dataarr.map(s=>{

        if(s.name.toLowerCase().includes(name.toLowerCase())){

                data.push(s)
     }

    })
 setData(data)
 setSubmit(true)
    console.log(data)
}
    return(
        <>
    {console.log(dataA)}
          <input type="text" value={name} onChange={(e)=>{setName(e.target.value)}} className="input1"/>
         <button disabled={name.length<3} onClick={fetchData}>Fetch</button>
         {dataA.length?
         <table>
         <tr>
           <th>ArticleId</th>
           <th>Name</th>
           <th>Title</th>
           <th>Upvotes</th>
           <th>Date</th>
         </tr>
         {dataA.map(i=>
            (
                <tr>
                <td>{i.articleId}</td>
                <td>{i.name}</td>
                <td>{i.title}</td>
                <td>{i.upvotes}</td>
                <td>{i.date}</td>
              </tr>
            ))}


       </table>
       :submit&&dataA?(<h3>No Results found</h3>):null}

        </>
    )
}
